////object oriented programming

class Hello {
	def log;
	def context;
	def testRunner;

	//construtor
	def Hello(log, context, testRunner){
		this.log=log
		this.context=context
		this.testRunner=testRunner
	}

	def sayHello(String name){
		log.info "Hello " + name
	}
}


context.setProperty("Hello", new Hello(log, context, testRunner))

//getting hold of testStep Hello //calling testStep from differnt testcase project = testRunner.testCase.testSuite.project tcase = project.testSuites["LearnGroovyBasics"].testCases["TestCase1"] hello = tcase.getTestStepByName("Hello")
hello.run(testRunner, context)
context.Hello.sayHello("John")

